import React, { useState, useEffect } from "react";

const images = [
  "üçé", "üçå", "üçí", "üçá", "üçâ", "ü•≠", "üçç", "üçë",
  "üçé", "üçå", "üçí", "üçá", "üçâ", "ü•≠", "üçç", "üçë"
];

const shuffleImages = () => {
  return [...images].sort(() => Math.random() - 0.5);
};

export default function MemoryMatch() {
  const [shuffledImages, setShuffledImages] = useState(shuffleImages());
  const [selected, setSelected] = useState([]);
  const [matched, setMatched] = useState([]);
  const [timeLeft, setTimeLeft] = useState(60);
  
  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    }
  }, [timeLeft]);
  
  const handleClick = (index) => {
    if (selected.length < 2 && !selected.includes(index) && !matched.includes(index)) {
      const newSelected = [...selected, index];
      setSelected(newSelected);
      
      if (newSelected.length === 2) {
        const [first, second] = newSelected;
        if (shuffledImages[first] === shuffledImages[second]) {
          setMatched([...matched, first, second]);
        }
        setTimeout(() => {
          setSelected([]);
          setShuffledImages(shuffleImages());
        }, 500);
      }
    }
  };

  return (
    <div className="flex flex-col items-center p-4">
      <h1 className="text-2xl font-bold mb-4">Memory Match Game</h1>
      <p className="text-lg">Time Left: {timeLeft}s</p>
      <div className="grid grid-cols-4 gap-3 mt-4">
        {shuffledImages.map((image, index) => (
          <div
            key={index}
            onClick={() => handleClick(index)}
            className="w-16 h-16 flex items-center justify-center border rounded-md text-2xl bg-gray-300 cursor-pointer"
          >
            {selected.includes(index) || matched.includes(index) ? image : "‚ùì"}
          </div>
        ))}
      </div>
    </div>
  );
}
